const togglbtn=document.querySelector(".toggle-btn"),body=document.querySelector("body"),toggleimg=document.querySelector(".toggle-img"),glitchTrigger=document.querySelectorAll(".hover-effect"),name=document.querySelector(".name"),root=document.documentElement;togglbtn.addEventListener("click",function(){body.classList.contains("dark")?(body.classList.remove("dark"),toggleimg.setAttribute("src","./assets/sun.svg")):(body.classList.add("dark"),root.style.setProperty("--background-color","#fff"),root.style.setProperty("--font-color","#3e3e3e"),toggleimg.setAttribute("src","https://img.icons8.com/carbon-copy/100/000000/crescent-moon.png"))}),glitchTrigger[0].addEventListener("mouseenter",()=>{name.classList.add("glitch"),root.style.setProperty("--background-color",returnColor()),root.style.setProperty("--font-color",returnColor())}),glitchTrigger[0].addEventListener("mouseleave",()=>{name.classList.remove("glitch")}),glitchTrigger[3].addEventListener("mouseenter",()=>{name.classList.add("glitch"),root.style.setProperty("--background-color",returnColor()),root.style.setProperty("--font-color",returnColor())}),glitchTrigger[3].addEventListener("mouseleave",()=>{name.classList.remove("glitch")});const random=()=>Math.floor(255*Math.random()),returnColor=()=>{let e=random(),t=random(),o=random();return`rgb(${e}, ${t}, ${o})`};